package com.problemsolving.arrays;public class RotateMatrix {    public static void main(String[] args) {        new RotateMatrix().rotate(new int[][]{{1, 2, 3}, {4, 5, 6}, {7, 8, 9}}, 3);    }    private void rotate(int[][] matrix, int n) {        for (int i = 0; i < n; i++) {            for (int j = i + 1; j < n; j++) {                swap(matrix, i, j);            }        }    }    private void swap(int[][] matrix, int i, int j) {        int temp = matrix[i][j];        matrix[i][j] = matrix[j][i];        matrix[j][i] = temp;    }}