package com.problemsolving.arrays;public class SpiralPrintOf2DArray {    public static void main(String[] args) {        new SpiralPrintOf2DArray().printSpiral(new int[][]{{1, 2,3}, {4,5, 6},{7,8,9}}, 3, 3);    }    void printSpiral(int[][] matrix, int row, int col) {        int T = 0;        int R = col - 1;        int L = 0;        int B = row - 1;        while (L <= R && T <= B) {            for (int i = L; i <= R; i++) {                System.out.println(matrix[T][i]);            }            T++;            for (int i = T; i <= B; i++) {                System.out.println(matrix[R][i]);            }            R--;            for (int i = R; i >= L; i--) {                System.out.println(matrix[i][B]);            }            B--;            for (int i = B; i >= T; i--) {                System.out.println(matrix[i][L]);            }            L++;        }    }}